dehydrated.css:
/*! tailwindcss v4.1.6 | MIT License | https://tailwindcss.com */
@layer theme, base, components, utilities;
@layer utilities;

appearance.stylesheet_hub2:
/* loader position */
.hub-loading-indicator {
	margin-top: 6rem;
}


/* search header */
#hub-search-results {
	margin-top: 11px;
  -webkit-box-shadow: 0 3px 3px 1px rgba(0,0,0,0.3);
  box-shadow: 0 3px 3px 1px rgba(0,0,0,0.3);
}

/* GET endpoint label */
.APIMethod_get {
  background: #03CA9B !important;
  text-shadow: 1px 1px 0 #03CA9B, 0 1px 0 #03CA9B, 1px 0 0 #03CA9B  !important;
}

/* sidebar dropdown */
li:has(ul.subpages) {
  font-weight: var(--font-weight-bold);
}

li:has(ul.subpages) ul {
  font-weight: 400;
}
li:has(ul.subpages) > a [data-testid="http-method"] {
  display: none;
}

/* link hower */
a:hover { 
  color: #01A781;
}

